import React, { Component } from 'react';
import 'materialize-css/dist/js/materialize';
import './../app.css'

class Searchbox extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             value: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.search = this.search.bind(this);
    }

    handleChange = (event) =>{
        this.setState({
            value : event.target.value
        })
    }

    search = () => {
        const base_url = "https://api.spotify.com/v1/search";
        fetch('https://api.spotify.com/v1/search?q=tania%20bowra&type=artist', {method : 'get'})
        .then(Response => Response.json())
        .then(json => console.log(json));
    }
    

    render() {
        return (
            <div>
                <form>
                    <div className="input-field">
                        <input type="text" className='validate' id="asearch" value={this.state.value} onChange={this.handleChange} />
                        <label htmlFor="asearch">Artist</label>
                        <button className='btn' onClick={this.search}>Search</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default Searchbox;
