import React, { Component } from 'react';
import 'materialize-css/dist/js/materialize';
import './../app.css';
import axios from 'axios';

class Searchbox extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             value: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.search = this.search.bind(this);
    }

    handleChange = (event) =>{
        this.setState({
            value : event.target.value
        })
    }

    componentDidMount(){
        this.search();
    }

    search = () => {
        fetch("https://deezerdevs-deezer.p.rapidapi.com/search?q=eminem", {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "deezerdevs-deezer.p.rapidapi.com",
		"x-rapidapi-key": "0368c42caemsh556d4e8c5c158d9p18e486jsn01ed5db22a6b"
	}
})
.then(response => {
	console.log(response.json());
})
.then(json => console.log(json))
    }

    cearch = () =>{
        axios.get('https://deezerdevs-deezer.p.rapidapi.com/search?q=eminem',{
            "method": "GET",
	        "headers": {"x-rapidapi-host": "deezerdevs-deezer.p.rapidapi.com",
		                "x-rapidapi-key": "0368c42caemsh556d4e8c5c158d9p18e486jsn01ed5db22a6b" }
        })
    }
    
    render() {
        return (
            <div>
                <form>
                    <div className="input-field">
                        <input type="text" className='validate' id="asearch" value={this.state.value} onChange={this.handleChange} />
                        <label htmlFor="asearch">Artist</label>
                        <button className='btn' onClick={this.search}>Search</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default Searchbox;
